my $FM;
if($FMFile ne ""){
  sysopen($FM, $FMFile, O_RDONLY);
  binmode($FM,":raw") || die "cannot binmode FM";
}

my $AM;
if($AMFile ne ""){
  sysopen($AM,$AMFile,O_RDONLY);
  binmode($AM,":raw") || die "cannot binmode AM";
}

binmode(STDOUT,":raw") || die "cannot binmode STDOUT";



my $skip = ($T * $F) / $S;
my $wait = 0;
my $i = 0;
my $FMScaleFactor = 1.0;
my $AMScaleFactor = 1.0;

while(1){


  my $sampfreq = $F;

  if( $FMFile ne "" ){

    my $fmraw;

    read($FM, $fmraw, $SampSize);

    my $fmval = unpack "S", $fmraw;

    $fmval = 0 if !defined $fmval;

    $FMScaleFactor = $fmval / 65536;

    $sampfreq = $F - (0.5 * $F * $FreqModDepth * $FMScaleFactor);

  }
 
 
  if( $AMFile ne "" ){

    if(eof($AM)){
      close($AM);
      sysopen($AM,$AMFile,O_RDONLY);
      binmode($AM,":raw") || die "cannot reinit AM pipe $!";
    }

    my $amraw;
    read($AM, $amraw, $SampSize); 
    my $amval = unpack "S", $amraw;
    $amval = 0 if !defined $amval;
    $AMScaleFactor = $amval / 65536;
  }
 
  $AMScaleFactor *= $AmpModDepth;

  my $samp;
  if($waveType == 0){ 

    $samp = ($sinTable[ $i ] * $A * $AMScaleFactor);

  }elsif($waveType == 1){

    $samp = ($triTable[ $i ] * $A * $AMScaleFactor);

  }else{
    
    $samp = ($squareTable[ $i ] * $A * $AMScaleFactor);

  }

  print pack 'S', $samp;

  $skip = $T * $sampfreq / $S;
  if( $skip >= 1 ){
    $wait = 0;
    $i += $skip;
  
    if( $i >= $waveTableSize ){
  
      $i = 0;
  
    } 

  }else{

    if($wait <= 0){

      $i++; 
  
      if( $i >= $waveTableSize ){
  
        $i = 0;
  
      } 
      #This will be > 1 because $skip < 1
      $wait = 1.0 / $skip;

    }else{
      $wait--;
    }

  }
  if($i < 0 || $i > $waveTableSize){
    die "wave table size violation\n"
  }

}
